mapper.select:
		select *
      from (select n.*, rownum as rnum from (select * from vwUserBuy where email = #{email} and buy_date >= sysdate - INTERVAL '14' DAY ) n)
      where rnum between #{startIndex} and #{endIndex}

		select *
      from (select n.*, rownum as rnum from (select * from vwUserBuy where email = #{email} and buy_date < sysdate - INTERVAL '14' DAY ) n)
      where rnum between #{startIndex} and #{endIndex}
